import PopupWithForm from "./components/PopupWithForm";

export default function Confirmation({ isOpen,  onClose, onSubmit }) {
  const handleSubmit = (evt) => {
    evt.preventDefault();
    onSubmit();
  };

  return (
    <PopupWithForm
      title='Удалить карточку?'
      name="re"
      submitStates='elfknm,'
      isOpen={isOpen}
button='Удалить'
      onClose={onClose}
      onSubmit={handleSubmit}
    />
  );
}
