import React, { useEffect, useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Main from "./Main";
import AddPlacePopup from "./AppPlacePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import EditProfilePopup from "./EditProfilePopup";
import "../index.css";
import { api } from "../utils/Api.js";
import Card from "./Card";

const App = () => {
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setisEditAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setisEditProfilePopupOpen] = useState(false);

  const [userName, setUserName] = useState([]);
  const [userDescription, setUserDescription] = useState([]);
  const [userAvatar, setUserAvatar] = useState([]);

  const [cardLikes, setCardLikes] = useState([]);
  const [cardLink, setCardLink] = useState([]);
  const [cardName, setCardName] = useState([]);

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setisEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setisEditProfilePopupOpen(true);
  }

  function closeAllPopups() {
    setisEditProfilePopupOpen(false);
    setisEditAvatarPopupOpen(false);
    setIsAddPlacePopupOpen(false);
  }

  useEffect(() => {
    api.getProfile().then((data) => {
      setUserName(data.name);
      setUserDescription(data.about);
      setUserAvatar(data.avatar);
    });
  });

  useEffect(() => {
    api.getInitialCards().then((data) => {
      setCardLikes(data.likes);
      setCardLink(data.link);
      setCardName(data.name);
    });
  });

  /*   function handleEditAvatarClick() {
    document
      .querySelector(".popup_type_change-avatar")
      .classList.add("popup_opened");
  } */

  return (
    <>
      <div className="body">
        <div className="page">
          <Header />
          <Main
            onEditAvatar={handleEditAvatarClick}
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            userAvatar={userAvatar}
            userDescription={userDescription}
            userName={userName}
          />

{/*           <template id="placeCards" className="square-card">
            <div className="card">
              <img className="card__image" style={{ backgroundImage: `url(${cardLink})` }} />
              <h2 className="card__place-name">{cardName}</h2>
              <button
                className="card__like-button"
                type="button"
                aria-label="поставить лайк"
              ></button>
              <span className="card__like-count">{cardLikes}</span>
              <button
                className="card__remove-button"
                type="button"
                aria-label="удалить карточку"
              ></button>
            </div>
          </template> */}
          <Footer />
          <AddPlacePopup
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
          />
          <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
          />
          <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
          />
        </div>
      </div>
    </>
  );
};

export default App;
