        return (
            <div className="card">
            <img className="card__image" src={link} />
            <h2 className="card__place-name">{name}</h2>
            <button
              className="card__like-button"
              type="button"
              aria-label="поставить лайк"
            ></button>
            <span className="card__like-count">{likes}</span>
            <button
              className="card__remove-button"
              type="button"
              aria-label="удалить карточку"
            ></button>
            </div> 
        )
    }
} */



 export default function Card({
card
}) {

return (
<div className="card">
<img className="card__image" src={card.link} />
<h2 className="card__place-name">{card.name}</h2>
<button
  className="card__like-button"
  type="button"
  aria-label="поставить лайк"
></button>
<span className="card__like-count">{card.likes}</span>
<button
  className="card__remove-button"
  type="button"
  aria-label="удалить карточку"
></button>
</div>
)
}
