export default function Confirmation({ isOpen,  onClose, onSubmit }) {
  const handleSubmit = (evt) => {
    evt.preventDefault();
    onSubmit();
  };

  return (
    <div
      className={`popup popup_type_change-avatar ${isOpen ? "popup_opened" : ""}`}
      name='change-avatar'
    >
      <div className="popup__container">
        <form
          id="formPlace"
          className={`popup__form popup__form_type_change-avatar`}
          name="addPlaceForm"
          noValidate
          onSubmit={onSubmit}
        >
          <button
            className="popup__button-close popup__button-close_type_create-place"
            aria-label="закрыть"
            type="button"
            onClick={onClose}
          ></button>
          <h2 className="popup__title">Удалить карточку? </h2>
          <button
            className="popup__button-submit popup__button-submit_type_card"
            type="submit"
            value="Создать"
          >
            Удалить
          </button>
        </form>
      </div>
    </div>
  );
}
