import React, { useEffect, useState, useContext } from "react";
import Card from "./Card";
import { api } from "../utils/api.js";
import { CurrentUserContext } from '../contexts/CurrentUserContext';


function Main({ onEditAvatar, onEditProfile, onAddPlace, onCardClick,  onCardLike }) {
  const currentUser = useContext(CurrentUserContext);
  const [cards, setCards] = useState([]);
  



  useEffect(() => {
    api
      .getInitialCards()
      .then((cards) => {
        setCards(cards);
      })
      .catch((err) => console.log("засада: " + err));
  }, []);

  return (
    <main className="content">
      <div className="profile">
        <div className="profile__author">
          <img
            className="profile__avatar"
            alt="профиль пользователя"
            src={currentUser.avatar}
          />
          <div className="profile__overlay" onClick={onEditAvatar}></div>
        </div>
        <div className="profile__info">
          <div className="profile__rename">
            <h1 className="profile__name">{currentUser.name}</h1>
            <button
              className="profile__edit-button"
              aria-label="изменить"
              type="button"
              onClick={onEditProfile}
            ></button>
          </div>
          <p className="profile__profession">{currentUser.about}</p>
        </div>
        <button
          className="profile__add-button"
          aria-label="добавить карточку"
          type="button"
          onClick={onAddPlace}
        ></button>
      </div>

      <article className="elements">
        {cards.map((card, i) => (
          <Card card={card} key={i} onCardClick={onCardClick}  onCardLike={onCardLike}></Card>
        ))}
      </article>
    </main>
  );
}

export default Main;
